"use strict";(self.webpackChunkgiliandrum=self.webpackChunkgiliandrum||[]).push([[563],{55595:e=>{e.exports=JSON.parse('{"version":{"pluginId":"default","version":"current","label":"Next","banner":null,"badge":false,"noIndex":false,"className":"docs-version-current","isLast":true,"docsSidebars":{"doboktatasSidebar":[{"type":"link","label":"\ud83e\udd41 \xdcdv\xf6zl\xfcnk a Gili\xe1n Dobiskol\xe1n\xe1l!","href":"/doboktatas/bevezeto","docId":"bevezeto","unlisted":false},{"type":"category","label":"\ud83e\udde9 Kezd\u0151 szint","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"Az els\u0151 l\xe9p\xe9sek a dobok vil\xe1g\xe1ban","href":"/doboktatas/kezdo/elso-lepesek","docId":"kezdo/elso-lepesek","unlisted":false},{"type":"link","label":"Milyen felszerel\xe9sre lesz sz\xfcks\xe9ged?","href":"/doboktatas/kezdo/felszereles","docId":"kezdo/felszereles","unlisted":false},{"type":"link","label":"C\xe9lok \xe9s tanul\xe1si m\xf3dszerek kezd\u0151knek","href":"/doboktatas/kezdo/celok-es-modszerek","docId":"kezdo/celok-es-modszerek","unlisted":false},{"type":"link","label":"A helyes tart\xe1s \xe9s ver\u0151fog\xe1s: Az alapok alapja","href":"/doboktatas/kezdo/helyes-tartas","docId":"kezdo/helyes-tartas","unlisted":false},{"type":"link","label":"A kottaolvas\xe1s alapjai dobosoknak","href":"/doboktatas/kezdo/kottaolvasas-alapjai","docId":"kezdo/kottaolvasas-alapjai","unlisted":false},{"type":"link","label":"Az elm\xe9lett\u0151l a gyakorlatig: Mire sz\xe1m\xedthatsz az els\u0151 \xf3r\xe1kon?","href":"/doboktatas/kezdo/gyakorlati-alkalmazas","docId":"kezdo/gyakorlati-alkalmazas","unlisted":false}],"href":"/doboktatas/kezdo-szint"},{"type":"category","label":"\ud83d\udd79\ufe0f K\xf6z\xe9phalad\xf3 szint","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"\xdcdv a k\xf6z\xe9phalad\xf3 szinten!","href":"/doboktatas/kozephalado/bevezetes","docId":"kozephalado/bevezetes","unlisted":false},{"type":"link","label":"Zenei st\xedlusok felfedez\xe9se","href":"/doboktatas/kozephalado/stilusok","docId":"kozephalado/stilusok","unlisted":false},{"type":"link","label":"A dobos zenei hall\xe1sa: Fejleszt\xe9s \xe9s fontoss\xe1g","href":"/doboktatas/kozephalado/zenei-hallas","docId":"kozephalado/zenei-hallas","unlisted":false},{"type":"link","label":"Groove \xe9s Fill: A dobos eszk\xf6zt\xe1ra","href":"/doboktatas/kozephalado/groove-es-fill","docId":"kozephalado/groove-es-fill","unlisted":false}],"href":"/doboktatas/kozephalado-szint"},{"type":"category","label":"\ud83c\udfc6 Halad\xf3 szint","collapsible":true,"collapsed":true,"items":[{"type":"link","label":"A technikai virtuozit\xe1s \xfatj\xe1n","href":"/doboktatas/halado/technikai-virtuozitas","docId":"halado/technikai-virtuozitas","unlisted":false},{"type":"link","label":"Komplex \xfctemmutat\xf3k \xe9s poliritmika","href":"/doboktatas/halado/komplex-utemmutatok","docId":"halado/komplex-utemmutatok","unlisted":false},{"type":"link","label":"A dobos, mint zeneszerz\u0151: Sz\xf3l\xf3\xe9p\xedtkez\xe9s \xe9s improviz\xe1ci\xf3","href":"/doboktatas/halado/szoloepites-improvizacio","docId":"halado/szoloepites-improvizacio","unlisted":false},{"type":"link","label":"A dobos szerepe a st\xfadi\xf3ban \xe9s a sz\xednpadon","href":"/doboktatas/halado/studio-es-szinpad","docId":"halado/studio-es-szinpad","unlisted":false}],"href":"/doboktatas/halado-szint"},{"type":"link","label":"\ud83d\ude80 Ne habozz, v\xe1gj bele!","href":"/doboktatas/zaras","docId":"zaras","unlisted":false}]},"docs":{"bevezeto":{"id":"bevezeto","title":"\ud83e\udd41 \xdcdv\xf6zl\xfcnk a Gili\xe1n Dobiskol\xe1n\xe1l!","description":"Ez a tud\xe1sb\xe1zis egyfajta digit\xe1lis k\xe9zik\xf6nyv, ami v\xe9gigk\xeds\xe9r a dobol\xe1s izgalmas \xfatj\xe1n, ak\xe1r a legels\u0151 l\xe9p\xe9seket teszed meg, ak\xe1r m\xe1r megl\xe9v\u0151 tud\xe1sodat szeretn\xe9d elm\xe9ly\xedteni. Ezen kereszt\xfcl szeretn\xe9m bemutatni neked a dobol\xe1s vil\xe1g\xe1t, \xe9s seg\xedteni abban, hogy megtal\xe1ld a sz\xe1modra legmegfelel\u0151bb tanul\xe1si utat, valamint, hogy mire sz\xe1m\xedthatsz a Gili\xe1n Dobiskol\xe1ban.","sidebar":"doboktatasSidebar"},"halado/komplex-utemmutatok":{"id":"halado/komplex-utemmutatok","title":"Komplex \xfctemmutat\xf3k \xe9s poliritmika","description":"A legt\xf6bb k\xf6nny\u0171zenei dal a j\xf3l megszokott 4/4-es \xfctemmutat\xf3ban \xedr\xf3dik. A halad\xf3 dobos azonban nem ijed meg, ha ett\u0151l el kell t\xe9rni. A p\xe1ratlan \xfctemmutat\xf3k \xe9s a poliritmika vil\xe1ga izgalmas zenei kalandokat rejt.","sidebar":"doboktatasSidebar"},"halado/studio-es-szinpad":{"id":"halado/studio-es-szinpad","title":"A dobos szerepe a st\xfadi\xf3ban \xe9s a sz\xednpadon","description":"Egy halad\xf3 dobos sz\xe1m\xe1ra a pr\xf3baterem mag\xe1nya ut\xe1n a k\xf6vetkez\u0151 l\xe9p\xe9s a st\xfadi\xf3 \xe9s a sz\xednpad. B\xe1r mindk\xe9t helyen a dobol\xe1s a feladat, a k\xf6rnyezet \xe9s az elv\xe1r\xe1sok gy\xf6keresen elt\xe9rnek. A professzion\xe1lis hozz\xe1\xe1ll\xe1s itt kulcsfontoss\xe1g\xfa.","sidebar":"doboktatasSidebar"},"halado/szoloepites-improvizacio":{"id":"halado/szoloepites-improvizacio","title":"A dobos, mint zeneszerz\u0151: Sz\xf3l\xf3\xe9p\xedtkez\xe9s \xe9s improviz\xe1ci\xf3","description":"A halad\xf3 dobos m\xe1r nem csak k\xeds\xe9r\u0151zen\xe9sz, hanem akt\xedv alkot\xf3. A dobsz\xf3l\xf3 \xe9s az improviz\xe1ci\xf3 az a ter\xfclet, ahol a technikai tud\xe1s, a zeneis\xe9g \xe9s a kreativit\xe1s tal\xe1lkozik. Itt mutathatod meg igaz\xe1n, ki vagy te, mint zen\xe9sz.","sidebar":"doboktatasSidebar"},"halado/technikai-virtuozitas":{"id":"halado/technikai-virtuozitas","title":"A technikai virtuozit\xe1s \xfatj\xe1n","description":"\xdcdv a halad\xf3 szinten! Itt m\xe1r nem csak a stabil alapokr\xf3l \xe9s a st\xedlusos j\xe1t\xe9kr\xf3l van sz\xf3, hanem a hangszeres tud\xe1sod hat\xe1rainak feszeget\xe9s\xe9r\u0151l. A c\xe9l a technikai virtuozit\xe1s el\xe9r\xe9se, ami azt jelenti, hogy b\xe1rmilyen zenei \xf6tletet k\xe9pes vagy magabiztosan \xe9s tiszt\xe1n megval\xf3s\xedtani a dobokon.","sidebar":"doboktatasSidebar"},"kezdo/celok-es-modszerek":{"id":"kezdo/celok-es-modszerek","title":"C\xe9lok \xe9s tanul\xe1si m\xf3dszerek kezd\u0151knek","description":"Most, hogy m\xe1r tudod, milyen felszerel\xe9sre van sz\xfcks\xe9ged, besz\xe9lj\xfcnk a tanul\xe1s folyamat\xe1r\xf3l \xe9s a re\xe1lis c\xe9lokr\xf3l. A Gili\xe1n Dobiskol\xe1ban a legfontosabb a fokozatoss\xe1g \xe9s a siker\xe9lm\xe9ny.","sidebar":"doboktatasSidebar"},"kezdo/elso-lepesek":{"id":"kezdo/elso-lepesek","title":"Az els\u0151 l\xe9p\xe9sek a dobok vil\xe1g\xe1ban","description":"\xdcdv\xf6z\xf6llek a Gili\xe1n Dobiskola inform\xe1ci\xf3s k\xf6zpontj\xe1ban! Ha most ismerkedsz a dobol\xe1s gondolat\xe1val, a legjobb helyen j\xe1rsz. Ez az \xfatmutat\xf3 seg\xedt meg\xe9rteni, mi\xe9rt fantasztikus hangszer a dob, \xe9s hogyan v\xe1gjatok bele a tanul\xe1sba.","sidebar":"doboktatasSidebar"},"kezdo/felszereles":{"id":"kezdo/felszereles","title":"Milyen felszerel\xe9sre lesz sz\xfcks\xe9ged?","description":"Az egyik leggyakoribb k\xe9rd\xe9s, amit a kezd\u0151 dobosok \xe9s sz\xfcleik feltesznek, a felszerel\xe9ssel kapcsolatos. L\xe1ssuk, mire van sz\xfcks\xe9ged az indul\xe1shoz!","sidebar":"doboktatasSidebar"},"kezdo/gyakorlati-alkalmazas":{"id":"kezdo/gyakorlati-alkalmazas","title":"Az elm\xe9lett\u0151l a gyakorlatig: Mire sz\xe1m\xedthatsz az els\u0151 \xf3r\xe1kon?","description":"Gratul\xe1lok! V\xe9gigolvastad a kezd\u0151 szekci\xf3 legfontosabb dokumentumait. Most m\xe1r van egy alapvet\u0151 elk\xe9pzel\xe9sed a helyes testtart\xe1sr\xf3l, a felszerel\xe9sekr\u0151l, a kottaolvas\xe1s alapjair\xf3l \xe9s a re\xe1lis c\xe9lokr\xf3l. Ez egy szuper alap, de a val\xf3di fejl\u0151d\xe9s akkor kezd\u0151dik, amikor ezt a tud\xe1st egy tan\xe1r seg\xedts\xe9g\xe9vel a gyakorlatba is \xe1t\xfcltetj\xfck.","sidebar":"doboktatasSidebar"},"kezdo/helyes-tartas":{"id":"kezdo/helyes-tartas","title":"A helyes tart\xe1s \xe9s ver\u0151fog\xe1s: Az alapok alapja","description":"A dobol\xe1s egy fizikai tev\xe9kenys\xe9g, ez\xe9rt a helyes testtart\xe1s \xe9s a laza, hat\xe9kony mozg\xe1s elengedhetetlen. Ezzel nem csak a s\xe9r\xfcl\xe9seket ker\xfclheted el, de a j\xe1t\xe9kod is sokkal foly\xe9konyabb \xe9s er\u0151teljesebb lesz.","sidebar":"doboktatasSidebar"},"kezdo/kottaolvasas-alapjai":{"id":"kezdo/kottaolvasas-alapjai","title":"A kottaolvas\xe1s alapjai dobosoknak","description":"Sokan idegenkednek a kottaolvas\xe1st\xf3l, pedig a dobosok sz\xe1m\xe1ra ez egy rendk\xedv\xfcl egyszer\u0171 \xe9s hasznos eszk\xf6z. A dobkotta nem bonyolult, \xe9s az alapjait b\xe1rki gyorsan elsaj\xe1t\xedthatja. Gondolj r\xe1 \xfagy, mint egy t\xe9rk\xe9pre, ami seg\xedt eligazodni a ritmusok vil\xe1g\xe1ban.","sidebar":"doboktatasSidebar"},"kozephalado/bevezetes":{"id":"kozephalado/bevezetes","title":"\xdcdv a k\xf6z\xe9phalad\xf3 szinten!","description":"Gratul\xe1lok, hogy eljutott\xe1l id\xe1ig! A k\xf6z\xe9phalad\xf3 szint egy izgalmas id\u0151szak, ahol a m\xe1r megszerzett alapokra \xe9p\xedtve elkezdj\xfck felfedezni a dobol\xe1s igazi m\xe9lys\xe9geit. Itt m\xe1r nem csak a \\"hogyan\\"-on van a hangs\xfaly, hanem a \\"mit\\" \xe9s a \\"mi\xe9rt\\"-en is.","sidebar":"doboktatasSidebar"},"kozephalado/groove-es-fill":{"id":"kozephalado/groove-es-fill","title":"Groove \xe9s Fill: A dobos eszk\xf6zt\xe1ra","description":"A dobol\xe1s k\xe9t alapvet\u0151 \xe9p\xedt\u0151k\xf6ve a groove \xe9s a fill. A k\xf6z\xe9phalad\xf3 szinten m\xe1r nem csak elj\xe1tsszuk \u0151ket, hanem tudatosan \xe9p\xedtkez\xfcnk bel\u0151l\xfck, \xe9s meg\xe9rtj\xfck a zenei funkci\xf3jukat.","sidebar":"doboktatasSidebar"},"kozephalado/stilusok":{"id":"kozephalado/stilusok","title":"Zenei st\xedlusok felfedez\xe9se","description":"A k\xf6z\xe9phalad\xf3 szinten m\xe1r nem csak az a k\xe9rd\xe9s, hogy tudsz-e dobolni, hanem az is, hogy mit dobolsz. A k\xfcl\xf6nb\xf6z\u0151 zenei st\xedlusok megismer\xe9se seg\xedt abban, hogy sokoldal\xfa \xe9s keresett doboss\xe1 v\xe1lj.","sidebar":"doboktatasSidebar"},"kozephalado/zenei-hallas":{"id":"kozephalado/zenei-hallas","title":"A dobos zenei hall\xe1sa: Fejleszt\xe9s \xe9s fontoss\xe1g","description":"J\xf3 doboss\xe1 v\xe1lni nem csak technikai k\xe9rd\xe9s. A zenei hall\xe1s, vagyis a k\xe9pess\xe9g, hogy felismerd, meg\xe9rtsd \xe9s reproduk\xe1ld a ritmusokat, legal\xe1bb annyira fontos. A k\xf6z\xe9phalad\xf3 szinten kiemelt figyelmet ford\xedtunk a hall\xe1s ut\xe1ni tanul\xe1sra \xe9s a bels\u0151 metron\xf3m fejleszt\xe9s\xe9re.","sidebar":"doboktatasSidebar"},"zaras":{"id":"zaras","title":"\ud83d\ude80 Ne habozz, v\xe1gj bele!","description":"Rem\xe9lem, hogy a tud\xe1sb\xe1zisban olvasottak hasznosak voltak sz\xe1modra, \xe9s siker\xfclt egy \xe1tfog\xf3 k\xe9pet kapnod arr\xf3l, mit is jelent a dobol\xe1s, \xe9s hogyan \xe9p\xfclnek fel az \xf3r\xe1im a Gili\xe1n Dobiskol\xe1ban.","sidebar":"doboktatasSidebar"}}}}')}}]);